import axios from 'axios';

export const getAllTickets = async () => {
  try {
    // Fetch the token dynamically, for example, from sessionStorage or a server response
    const token = sessionStorage.getItem('accessToken'); // Replace with your actual token source
 console.log (token)
    // Check if the token exists
    if (!token) {
      throw new Error('Access token is missing.'); // Handle the case where the token is missing
    }

    const response = await axios.get('http://localhost:5000/ticket', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    // If the request is successful, return the response data
    return response.data;
  } catch (error) {
    // If there is an error, reject the promise with the error object
    throw error;
  }
};
