import React from 'react';
import PropTypes from 'prop-types';
import { Button, Form, FormLabel, Card } from 'react-bootstrap';
import "./addticketform.style.css";


export const AddTicketForm = ({ handleOnSubmit, handleOnChange, data }) => {
  return (
    <Card className="mt-3 add-new-ticket bg-light">
      <h1 className='text-info text-center'>ADD NEW TICKET</h1>
      <form autoComplete='off' onSubmit={handleOnSubmit}>
        <Form.Group>
          <FormLabel>Subject</FormLabel>
          <Form.Control
            minLength={3}
            name='subject'
            value={data.subject}
            onChange={handleOnChange}
            required
          />
        </Form.Group>

        <Form.Group>
          <FormLabel>Sender</FormLabel>
          <Form.Control
            as='textarea'
            name='sender'
            onChange={handleOnChange}
            value={data.sender}
            required
          />
        </Form.Group>

        <Form.Group>
          <FormLabel>Details</FormLabel>
          <Form.Control
            as='textarea'
            name='message'
            onChange={handleOnChange}
            value={data.message}
            required
          />
        </Form.Group>

        <Button type='submit' variant='info' className="btn btn-primary btn-lg btn-block">
          ADD Ticket
        </Button>
      </form>

      {/* I removed the unnecessary <Link> component here */}
    </Card>
  );
};

AddTicketForm.propTypes = {
  handleOnSubmit: PropTypes.func.isRequired,
  handleOnChange: PropTypes.func.isRequired,
 // Assuming data is an object with subject, sender, and message fields.
};
