import { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";

export const Login = ({handleOnChange,handleOnSubmit,formSwitcher, email,pass} ) => {
 
    const [data, setData] = useState({
		name:"",
company:"",
address:"",
phone:"",
email:"",
password:"",
	});
	const [error, setError] = useState("");
	const navigate = useNavigate();

	const handleChange = ({ currentTarget: input }) => {
		setData({ ...data, [input.name]: input.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault(); // Empêche le comportement de soumission par défaut du formulaire
		try {
		  const url = "http://localhost:5000/auth/register";
		  
		  // Vous devrez remplacer `data` par les données que vous souhaitez envoyer dans la requête
		  // Par exemple : const data = { username: "john", password: "secret" };
		  
		  const { data: res } = await axios.post(url, data);
		  
		  // Après avoir envoyé la requête avec succès, redirige vers "/entry"
		  navigate("/entry");
		  
		  console.log(res.message); // Affiche le message de la réponse
		} catch (error) {
		  if (
			error.response &&
			error.response.status >= 400 &&
			error.response.status <= 500
		  ) {
			setError(error.response.data.message); // Affiche le message d'erreur du serveur
		  }
		  console.log(error); // Affiche l'erreur dans la console
		}
	  };
	  

	return (
        <div className={styles.signup_container}>
        <div className={styles.signup_form_container}>
				<div className={styles.left}>
					<h1>Welcome Back</h1>
					<Link to="/entry">
						<button type="button" className={styles.white_btn}>
							Sing in
						</button>
					</Link>
				</div>
				 <div className={styles.right}>
                    <form className={styles.form_container} onSubmit={handleSubmit}>
						<h1>Create Account</h1>
						<input
                            type="text"
                            placeholder="First Name"
                            name="Name"
                            onChange={handleChange}
                            value={data.firstName}
                            required
                            className={styles.input}
                        />
                        <input
                            type="text"
                            placeholder="Company"
                            name="company"
                            onChange={handleChange}
                            value={data.company}
                            required
                            className={styles.input}
                        />
								<input
							type="phone"
							placeholder="phone"
							name="phone"
							onChange={handleChange}
							value={data.phone}
							required
							className={styles.input}
						/>
						<input
							type="email"
							placeholder="Email"
							name="email"
							onChange={handleChange}
							value={data.email}
							required
							className={styles.input}
						/>
						<input
							type="password"
							placeholder="Password"
							name="password"
							onChange={handleChange}
							value={data.password}
							required
							className={styles.input}
						/>
						{error && <div className={styles.error_msg}>{error}</div>}
						<button type="submit"  className={styles.green_btn}>
							Sing Up
						</button>
					</form>
				</div>
			</div>
		</div>
	);
};
