import React ,{ useState,useEffect }from 'react'

import axios from "axios";
import { Link } from "react-router-dom";
import {AddTicketForm }from '../../components/add-ticket-form/AddTicketForm.comp'
import { Container, Row, Col } from "react-bootstrap";

const initialFrmDt ={
subject:"",
issueDate:"",
details:"",

}


export const AddTicket = () => {
const [frmData,setFrmData] =useState(initialFrmDt)
useEffect(()=>{},[frmData])
const handleOnChange = (e) => {
  const { name, value } = e.target;

  setFrmData({
    ...frmData,
    [name]: value,
  });
};
const handleOnSubmit = async (e) => {
  e.preventDefault();
  try {
    const url = "http://localhost:5000/ticket";
    const response = await axios.post(url, data);
    // Assuming you get a token in the response, you can extract it like this:
    const token = response.data.token;
    
    // Store the token in localStorage
    localStorage.setItem("token", token);
    
    // Redirect to a new page or do something else
    // Example redirect:
    window.location = "/add-ticket"; // Change the URL to the desired destination
  } catch (error) {
    if (
      error.response &&
      error.response.status >= 400 &&
      error.response.status <= 500
    ) {
      // Handle the error here (e.g., display an error message)
      console.error(error.response.data.message);
    }
  }
};


  






  return (
    <Container>
     <Row>
      <Col>
        <AddTicketForm  handleOnChange={handleOnChange}
                        handleOnSubmit={handleOnSubmit}
                         frmData={frmData}
        
        
        
        />
      </Col>
    </Row>
  </Container>
  )
}
