import React,{ useState } from 'react' 
import './entry.style.css'
import { Card } from 'react-bootstrap';

import { RegisterCom } from '../../components/register/RegisterCom';

export const Register = () => {


const [name,setName]=useState("");
const [Company, setCompany]=useState("");

const [phone,setPhone]=useState("");
const [email,setEmail]=useState("");
const [password, setPassword]=useState("");




const [frmLoad, setFrmLoad] = useState("RegisterCom");



const handleOnChange = e => {
  const { name, value } = e.target;

  if (name === 'email') {
    setEmail(value);
  } else if (name === 'password') {
    setPassword(value);
  }
  
};

const handleOnSubmit = e => {
  e.preventDefault();
  if (!email || !password) {
    alert("Please fill up all the fields."); // Utilisation correcte de la fonction alert
    return;
  }
  // TODO: CALL API TO SUBMIT THE FORM
  console.log(email, password);
};

const formSwitcher=(frmType)=>{
  setFrmLoad(frmType);
}
const handleOnResetSubmit = e => {
  e.preventDefault();
  if (!email ) {
    alert("Please enter th email."); // Utilisation correcte de la fonction alert
    return;
  }
  // TODO: CALL API TO SUBMIT THE FORM
  console.log(email);
};



  return (
    <div className='entry-page '>
    
    <Card>
    {frmLoad === "RegisterCom " && (
          <RegisterCom
            handleOnChange={handleOnChange}
            handleOnSubmit={handleOnSubmit}
            formSwitcher={formSwitcher}
			name={name}
            company={company}
            address={address}
            phone={phone}
            email={email}
            pass={password}
          />
        )}
    

   



    
       
    </Card>
    
    
    
    </div>




    )
}
