import { createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';
import { fetchTicketFail, fetchTicketLoading, fetchTicketSuccess } from './ticketSlice';

export const fetchAlltickets = createAsyncThunk(
  'tickets/fetchAlltickets',
  async (_, { dispatch }) => {
    dispatch(fetchTicketLoading());

    try {
      const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJVc2VySW5mbyI6eyJpZCI6IjY0ZWE5ZDExYzc3YmJhYjEyZDAwYmQwMCJ9LCJpYXQiOjE2OTQyNjc0NDUsImV4cCI6MTY5NDI2ODM0NX0.dmU8dzh0KrsBqpbfp439jKwYg4lkAk1HGzOqrrTpvJk'; // Replace with your actual token
      const response = await axios.get('http://localhost:5000/ticket', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      const data = response.data;
      console.log(data);
      dispatch(fetchTicketSuccess(data.billets));
    } catch (error) {
      dispatch(fetchTicketFail(error.message));
    }
  }
);
