import React,{ useState } from 'react' 
import './entry.style.css'
import { Card } from 'react-bootstrap';

import { RegisterCom } from '../../components/register/RegisterCom';

export const Register = () => {


const [name,setName]=useState("");
const [company, setCompany]=useState("");

const [phone,setPhone]=useState("");
const [email,setEmail]=useState("");
const [password, setPassword]=useState("");




const [frmLoad, setFrmLoad] = useState("RegisterCom");


const handleOnChange = e => {
	const { name, value } = e.target;
  
	const inputMap = {
	  name: setName,
	  company: setCompany,
	  phone: setPhone,
	  email: setEmail,
	  password: setPassword
	};
  
	const setter = inputMap[name];
	if (setter) {
	  setter(value);
	}
  };

const handleOnSubmit = e => {
  e.preventDefault();
  if (!name||!company||!phone||!email || !password ) {
    alert("Please fill up all the fields."); // Utilisation correcte de la fonction alert
    return;
  }
  // TODO: CALL API TO SUBMIT THE FORM
  console.log(name,company,phone,email, password);
};

const formSwitcher=(frmType)=>{
  setFrmLoad(frmType);
}



  return (
    <div className='entry-page '>
    
    <Card>
    {frmLoad === "RegisterCom " && (
          <RegisterCom
            handleOnChange={handleOnChange}
            handleOnSubmit={handleOnSubmit}
            formSwitcher={formSwitcher}
			name={name}
            company={company}
            phone={phone}
            email={email}
            pass={password}
          />
        )}
    

   



    
       
    </Card>
    
    
    
    </div>




    )
}
