import {getAllTickets} from '../../api/ticketApi'
import { fetchTicketFail, fetchTicketLoading, fetchTicketSuccess } from './ticketSlice';

export const fetchAlltickets  = () => async (dispatch) => {
  dispatch(fetchTicketLoading());

  try {
    const response = await getAllTickets();
    if (response.status === "success") {
      const data = response.data;
      console.log(data); // Vérifiez la valeur de data
      dispatch(fetchTicketSuccess(data));
    } else {
      console.log("Réponse de l'API avec un statut d'erreur.");
      dispatch(fetchTicketFail("Réponse de l'API avec un statut d'erreur."));
    }
  } catch (error) {
    console.error("Erreur lors de la requête à l'API :", error);
    dispatch(fetchTicketFail("Erreur lors de la requête à l'API."));
  }
  
  }


